function removeDuplicateCharacters(string) {
    var promise = new Promise(function (resolve, reject) {
        var unique = '';
        for (var i = 0; i < string.length; i++) {
            if (unique.indexOf(string[i]) == -1) {
                unique += string[i];
            }
        }
        resolve(unique)
    })
    return promise;
}



